import React from "react";
import axios from 'axios';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';



function Register() {

    const onSubmit = data => {
        console.log("data local",data)
        axios.post('Contact.php', data)
      .then(res => {
        console.log("data",res.data);
        console.log(res.data);
      })
       .catch(err => console.log(err));
     };

     const validationSchema = Yup.object().shape({
        fullname: Yup.string().required('Fullname is required'),
        username: Yup.string()
            .required('Username is required')
            .min(6, 'Username must be at least 6 characters')
            .max(20, 'Username must not exceed 20 characters'),
        email: Yup.string()
            .required('Email is required')
            .email('Email is invalid'),
        password: Yup.string()
            .required('Password is required')
            .min(6, 'Password must be at least 6 characters')
            .max(40, 'Password must not exceed 40 characters'),
            confirm_password: Yup.string()
            .required('Confirm Password is required')
            .oneOf([Yup.ref('password'), null], 'Confirm Password does not match'),
        acceptTerms: Yup.bool().oneOf([true], 'Accept Terms is required')
    });

    const { register, handleSubmit, reset, formState: { errors } } = useForm({ resolver: yupResolver(validationSchema) });


    return (
        <div className="container py-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h4>Register</h4>
                        </div>
                        <div className="card-body">
                            <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                <div className="form-group mb-3">
                                    <label>Full Name:</label>
                                    <input type="text" name="fullname" {...register('fullname')} className={`form-control ${errors.fullname ? 'is-invalid' : ''}`} value="" />
                                    <div className="invalid-feedback">{errors.fullname?.message}</div>
                                </div>

                                <div className="form-group mb-3">
                                    <label>Email:</label>
                                    <input type="email" name="email" {...register('email')} className={`form-control ${errors.email ? 'is-invalid' : ''}`} value="" />
                                    <div className="invalid-feedback">{errors.email?.message}</div>
                                </div>

                                <div className="form-group mb-3">
                                    <label>Password:</label>
                                    <input type="password" name="password" {...register('password')} className={`form-control ${errors.password ? 'is-invalid' : ''}`} value="" />
                                    <div className="invalid-feedback">{errors.password?.message}</div>
                                </div>

                                <div className="form-group mb-3">
                                    <label>Confirm Password:</label>
                                    <input type="password" name="confirm_password" {...register('confirm_password')} className={`form-control ${errors.confirm_password ? 'is-invalid' : ''}`} value="" />
                                    <div className="invalid-feedback">{errors.confirm_password?.message}</div>
                                </div>
                                <div className="form-group mb-3">
                                    <button type="submit" className="btn btn-primary">Register</button>
                                </div>
                            </form>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Register;
