import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { useNavigate } from "react-router-dom";

import { store } from "react-notifications-component";
import Navbar from "../../../layouts/frontend/Navbar";
// import { store } from "react-notifications-component";
// import Navbar from "../../../layouts/frontend/Navbar";

const AddUsers = () => {

    let navigate = useNavigate();
    const onSubmit = data => {
        console.log("test image data",data)
        // axios.get('/sanctum/csrf-cookie').then(response => {
        //     axios.post('/api/register', data)

        //         .then(res => {

        //             if (res.data.status === 200) {
        //                 localStorage.setItem('auth_token', res.data.token);
        //                 localStorage.setItem('auth_name', res.data.username);
        //                 navigate('/login');
        //                 store.addNotification({
        //                     title: "Success!",
        //                     message: "Registered Successfully",
        //                     type: "success",
        //                     insert: "top",
        //                     container: "top-right",
        //                     animationIn: ["animated", "fadeIn"],
        //                     animationOut: ["animated", "fadeOut"],
        //                     dismiss: {
        //                         duration: 3000,
        //                         onScreen: false
        //                     }
        //                 });
        //             }

        //             if (res.data.status === 422) {

        //                 reset({ name: res.data.name, password: res.data.password, confirm_password: res.data.confirm_password });
        //                 store.addNotification({

        //                     title: "Failed!",
        //                     message: res.data.message.email[0],
        //                     type: "danger",
        //                     insert: "top",
        //                     container: "top-right",
        //                     animationIn: ["animated", "fadeIn"],
        //                     animationOut: ["animated", "fadeOut"],
        //                     dismiss: {
        //                         duration: 3000,
        //                         onScreen: false
        //                     }
        //                 });

        //             }

        //         })
        //         .catch(err => console.log(err));
        // });
    };
    const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/
    const validationSchema = Yup.object().shape({
        phoneNumber: Yup.string().matches(phoneRegExp, 'Phone number is not valid')
        .min(10, "too short")
        .max(10, "too long"),
        // name: Yup.string().required('Fullname is required'),
        // username: Yup.string()
        //     .required('Username is required')
        //     .min(6, 'Username must be at least 6 characters')
        //     .max(20, 'Username must not exceed 20 characters'),
        image: Yup.mixed()
        .test(
          "numberOfFiles",
          "Image is required",
          (value) => {
            if (value.length > 0) return true;
            return false;
          }
        ).test("fileSize", "error information", (value) => {
            if (value.length > 0) return value[0].size <= 5242880;
            return false;
          }).test(
            "fileType",
            "error information",
            (value) => {
              if (value.length === 0) return false;
              for (var i = 0; i < value.length; i++) {
                if (
                  !["image/jpeg", "image/png", "image/jpg"].includes(value[i].type)
                )
                  return false;
              }
              return true;
            }
          ),

        // email: Yup.string()
        //     .required('Email is required')
        //     .email('Email is invalid'),

        doj: Yup.string().required('DOJ is required'),

        document: Yup.mixed()
        .test(
          "numberOfFiles",
          "CV is required",
          (value) => {
            if (value.length > 0) return true;
            return false;
          }
        ).test("fileSize", "error information", (value) => {
            if (value.length > 0) return value[0].size <= 5242880;
            return false;
          }).test(
            "fileType",
            "error information",
            (value) => {
              if (value.length === 0) return false;
              for (var i = 0; i < value.length; i++) {
                if (
                  !["application/pdf", "application/doc"].includes(value[i].type)
                )
                  return false;
              }
              return true;
            }
          ),

        password: Yup.string()
            .required('Password is required')
            .min(8, 'Password must be at least 8 characters')
            .max(40, 'Password must not exceed 40 characters'),
        confirm_password: Yup.string()
            .required('Confirm Password is required')
            .oneOf([Yup.ref('password'), null], 'Confirm Password does not match'),
        // acceptTerms: Yup.bool().oneOf([true], 'Accept Terms is required')
    });

    const { register, handleSubmit, reset, formState: { errors } } = useForm({ resolver: yupResolver(validationSchema) });


    return (


        <div className="text-dark">

            <button type="button" className="btn btn-primary rounded-0" data-bs-toggle="modal" data-bs-target="#exampleModal">Add User</button>


            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content rounded-0">
                        <div className="modal-header bg-primary rounded-0">
                            <h5 className="modal-title" id="exampleModalLabel">Add user information</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">

                        <div className="card-body">
                                <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                    <div className="form-group mb-3">
                                        <label>Phone Number :</label>
                                        <input type="text" name="phoneNumber" {...register('phoneNumber')} className={`form-control rounded-0 ${errors.phoneNumber ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.phoneNumber?.message}</div>
                                    </div>

                                    <div className="form-group mb-3">
                                        <label>DOJ :</label>
                                        <input type="date" name="doj" {...register('doj')} className={`form-control rounded-0 ${errors.doj ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.doj?.message}</div>
                                    </div>

                                    <div className="form-group mb-3">
                                        <label>Image:</label>
                                        <input type="file" name="image" {...register('image')} className={`form-control rounded-0 ${errors.image ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.image?.message}</div>
                                    </div>

                                    <div className="form-group mb-3">
                                        <label>CV :</label>
                                        <input type="file" name="document" {...register('document')} className={`form-control rounded-0 ${errors.document ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.document?.message}</div>
                                    </div>


                                    <div className="form-group mb-3">
                                        <label>Password:</label>
                                        <input type="password" name="password" {...register('password')} className={`form-control rounded-0 ${errors.password ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.password?.message}</div>
                                    </div>

                                    <div className="form-group mb-3">
                                        <label>Confirm Password:</label>
                                        <input type="password" name="confirm_password" {...register('confirm_password')} className={`form-control rounded-0 ${errors.confirm_password ? 'is-invalid' : ''}`} />
                                        <div className="invalid-feedback">{errors.confirm_password?.message}</div>
                                    </div>
                                    <div className="form-group mb-3">
                                        <button type="submit" className="btn btn-primary rounded-0">Register</button>
                                        {/* <button type="button" onClick={reset} className="btn btn-warning float-right ms-2">Reset</button> */}
                                    </div>
                                </form>
                            </div>

                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default AddUsers;
