import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

import { useFormik } from 'formik';
import * as yup from 'yup'


// import { store } from "react-notifications-component";
// import Navbar from "../../../layouts/frontend/Navbar";

const AddUsers = () => {

    yup.addMethod( yup.mixed, 'file', function( error_message ) {
        return this.test( 'test-1', error_message, function( value ) {
            return ( value instanceof File )
        } )
    } )

    yup.addMethod( yup.mixed, 'allowedMime', function( mime, error_message=null  ) {
        return this.test( 'test-1', error_message, function( value ) {
            if( !value ){
                return
            }
            let _mime = value.type.split("/")[0]
            let { path, createError } = this
            return (
                _mime === mime ||
                createError( { path, message: ( error_message != null )? error_message : `Only ${mime} type of files are allowed` } )
            )
        } )
    } )




    return (
        <div className="text-dark">

            <button type="button" className="btn btn-primary rounded-0" data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-whatever="@mdo">Add User</button>


            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content rounded-0">
                        <div className="modal-header bg-primary rounded-0">
                            <h5 className="modal-title" id="exampleModalLabel">New message</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">

                            <form onSubmit={handleSubmit}>

                                <div className="form-group">
                                    <label>Contact No. :</label>

                                    <input type="text" name="name" className="form-control rounded-0" onChange={(e) => setName(e.target.value)} placeholder="Full Name" value={name || ''} required />
                                    {errors.name && <p>{errors.name.message}</p>}
                                </div>



                                <div className="form-group mt-2">
                                    <label>Email</label>

                                    <input type="text" name="email" className="form-control rounded-0" onChange={(e) => setEmail(e.target.value)} placeholder="Email" value={email || ''} required />

                                </div>

                                <div className="form-group mt-2">
                                    <label>Contact no.</label>

                                    <input type="text" name="phonenumber" className="form-control rounded-0" onChange={(e) => setPhonenumber(e.target.value)} placeholder="Contact no." value={phonenumber || ''} required />
                                </div>

                                <div className="form-group mt-2">
                                    <label>Upload CV</label>
                                    <input type="file" name="file" className="form-control rounded-0" onChange={(e) => setSelectedFile(e.target.files[0])} ref={imageInputRef} required />
                                </div>



                                <div className="form-group mt-2">
                                    <button type="submit" className="btn btn-success" name="submit">
                                        Submit
                                    </button>
                                </div>

                            </form>

                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default AddUsers;
