import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

import { useFormik } from 'formik';
import * as yup from 'yup'


// import { store } from "react-notifications-component";
// import Navbar from "../../../layouts/frontend/Navbar";

const AddUsers = () => {

    const formik = useFormik({
        initialValues : {
            name : '',
            price : '',
            image : '',
        },
        validate : values => {
            let errors = {}

            if( values.name.length === 0 ){
                errors.name = "This field is required"
            }

            if( values.price.length === 0 ){
                errors.price = "This field is required"
            }

            if( values.image.length === 0 ){
                errors.image = "This is required."
            } else {
                let type = values.image.type.split("/")[0]

                if( type !== "image" ){
                    errors.image = "The Uploaded file must be a Image."
                }
            }

            return errors
        },
        onSubmit: values => {
            console.log(`values`, values)
            // console.log(`formik`, formik)
        }
    })
    function validateContact() {
        var valid = true;	
        $(".demoInputBox").css('background-color','');
        $(".info").html('');
        if(!$("#firstname").val()) {
            $("#firstName-info").html("*first name required.");
            $("#firstname");
            valid = false;
        }
        if(!$("#lastname").val()) {
            $("#lastName-info").html("*last name required.");
            $("#lastname");
            valid = false;
        }
        if(!$("#selectCountry").val()) {
            $("#selectCountry-info").html("*country code required.");
            $("#selectCountry");
            valid = false;
        }

        if(!$("#phonenumber").val()) {
            $("#phoneNumber-info").html("*phone no. required.");
            $("#phonenumber");
            valid = false;
        }
        if(!$("#email").val()) {
            $("#email-info").html("*email required.");
            $("#email");
            valid = false;
        }
        if(!$("#email").val().match(/^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/)) {
            $("#email-info").html("(invalid)");
            $("#email");
            valid = false;
        }
        if(!$("#message").val()) {
            $("#message-info").html("*message required.");
            $("#message");
            valid = false;
        }
        return valid;
    }

    return (


        <div className="text-dark">

            <button type="button" className="btn btn-primary rounded-0" data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-whatever="@mdo">Add User</button>


            <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content rounded-0">
                        <div className="modal-header bg-primary rounded-0">
                            <h5 className="modal-title" id="exampleModalLabel">New message</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">

                        <form onSubmit={formik.handleSubmit} >
                    <div>
                        <label>Product Name</label>
                        <div>
                            <input onChange={formik.handleChange} name="name" type="text" value={formik.values.name} />
                            { formik.errors.name ? <div>{ formik.errors.name }</div> : null }
                        </div>
                    </div>

                    <div>
                        <label>Product Price</label>
                        <div>
                            <input onChange={formik.handleChange} name="price" value={formik.values.price} type="number" step="any" />
                            { formik.errors.price ? <div>{ formik.errors.price }</div> : null }
                        </div>
                    </div>

                    <div>
                        <label>Product Image</label>
                        <div>
                            <input type="file" onChange={ (e) => formik.setFieldValue( "image", e.target.files[0] ) } name="image" />
                            { formik.errors.image ? <div>{ formik.errors.image }</div> : null }
                        </div>
                    </div>


                    <div>
                        <input type="submit" className="mt-20" value="Save" />
                    </div>

                </form>

                        </div>

                    </div>
                </div>
            </div>
        </div>
    )
}

export default AddUsers;
