import React,{useState, useEffect} from "react";
import { Link } from 'react-router-dom';
import axios from "axios";
import {Modal} from 'react-bootstrap';

import AddUsers from "./AddUsers";
// import { Link } from 'react-router-dom';
const ViewUsers = () => {

const [loading,setLoading] = useState(true);
const [users, setUsers] = useState([]);

const [show, setShow] = useState(false);
    const handleShow = () => setShow(false);
    const handleClose = () => setShow(false);

    useEffect(() => {
        axios.get('/api/view-users').then(res=> {
            // console.log("test data",res.data.users);
            if(res.data.status === 200){
                setUsers(res.data.users);
            }
            setLoading(false);
        });
        // axios.post('/api/add_users_info/2').then(res=> {
        //     console.log("user info test data",res.data.message);


        // });
    },[]);

    var viewUsersList_HTMLTABLE = "";
    if(loading){
        return <h4>Loading Users...</h4>
    }
    else{
        viewUsersList_HTMLTABLE = 
        users.map( (item) => {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>
                    <button onClick={handleShow}> Show Modal </button>
                    </td>
                    <td>
                        <Link to={`edit_user/${item.id}`} className="btn btn-success btn-sm"> Edit
                        </Link>
                    </td>
                    <td><button type="button" className="btn btn-danger btn-sm">Delete
                        </button></td>

                        <Modal className="my-modal" show={show} onHide={handleClose}>

<Modal.Header closeButton className="mymodal-head">
    <Modal.Title className="mymodal-title"><h4>The Modal</h4></Modal.Title>

</Modal.Header>

<Modal.Body className="mymodal-body">

    <AddUsers />
</Modal.Body>

<Modal.Footer className="mymodal-footer">
//add your submit button here
    <button> submit </button>
</Modal.Footer>

</Modal>
                    </tr>
            )
        });
    }
    return (

        <div className="container py-4">

           <div className="card mt-4">
                <div className="card-header">
                    <h4>Users List</h4>
                    <AddUsers></AddUsers>

                </div>

                <div className="card-body">
                    <table className="table table-bordered table-triped">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Add Info</th>
                                <th>Edit</th>
                                <th>Delete</th>

                            </tr>
                        </thead>
                        <tbody>
                            {viewUsersList_HTMLTABLE}
                        </tbody>
                    </table>
                </div>
           </div>

        </div>
    );
}

export default ViewUsers;
