import React,{useState, useEffect} from "react";
import { Link } from 'react-router-dom';
import axios from "axios";

import AddUsers from "./AddUsers";
// import { Link } from 'react-router-dom';
const ViewUsers = () => {

const [loading,setLoading] = useState(true);
const [users, setUsers] = useState([]);

    useEffect(() => {
        axios.get('/api/view-users').then(res=> {
            // console.log("test data",res.data.users);
            if(res.data.status === 200){
                setUsers(res.data.users);
            }
            setLoading(false);
        });
        // axios.post('/api/add_users_info/2').then(res=> {
        //     console.log("user info test data",res.data.message);


        // });
    },[]);

    var viewUsersList_HTMLTABLE = "";
    if(loading){
        return <h4>Loading Users...</h4>
    }
    else{
        viewUsersList_HTMLTABLE = 
        users.map( (item) => {
            return (
                <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>
                    <AddUsers item={item}></AddUsers>
                    </td>
                    <td>
                        <Link to={`edit_user/${item.id}`} className="btn btn-success btn-sm"> Edit
                        </Link>
                    </td>
                    <td><button type="button" className="btn btn-danger btn-sm">Delete
                        </button></td>
                    </tr>
            )
        });
    }
    return (

        <div className="container py-4">

           <div className="card mt-4">
                <div className="card-header">
                    <h4>Users List</h4>
                    <AddUsers></AddUsers>

                </div>

                <div className="card-body">
                    <table className="table table-bordered table-triped">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Add Info</th>
                                <th>Edit</th>
                                <th>Delete</th>

                            </tr>
                        </thead>
                        <tbody>
                            {viewUsersList_HTMLTABLE}
                        </tbody>
                    </table>
                </div>
           </div>


           <button type="button" className="btn btn-primary rounded-0" data-bs-toggle="modal" data-bs-target="#exampleModal">Add User</button>


<div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog">
        <div className="modal-content rounded-0">
            <div className="modal-header bg-primary rounded-0">
                <h5 className="modal-title" id="exampleModalLabel">Add user information</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">

            <div className="card-body">
                    <form onSubmit={handleSubmit(onSubmit)} method="POST">
                        <div className="form-group mb-3">
                            <label>Phone Number :</label>
                            <input type="text" name="phoneNumber" {...register('phoneNumber')} className={`form-control rounded-0 ${errors.phoneNumber ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.phoneNumber?.message}</div>
                        </div>

                        <div className="form-group mb-3">
                            <label>DOJ :</label>
                            <input type="date" name="doj" {...register('doj')} className={`form-control rounded-0 ${errors.doj ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.doj?.message}</div>
                        </div>

                        <div className="form-group mb-3">
                            <label>Image:</label>
                            <input type="file" name="image" {...register('image')} className={`form-control rounded-0 ${errors.image ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.image?.message}</div>
                        </div>

                        <div className="form-group mb-3">
                            <label>CV :</label>
                            <input type="file" name="document" {...register('document')} className={`form-control rounded-0 ${errors.document ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.document?.message}</div>
                        </div>

                        <div className="form-group mb-3">
                            <label>Gender :</label>
                            <select type="text" name="gender" {...register('gender')} className={`form-control select rounded-0 ${errors.gender ? 'is-invalid' : ''}`}>
                                <option value="">Select Gender</option>
                                <option>Male</option>
                                <option>Female</option>
                            </select>
                            <div className="invalid-feedback">{errors.gender?.message}</div>
                        </div>

                    <div className="form-group mb-3">
                            <label>About :</label>
                            <textarea type="text" name="about" {...register('about')} className={`form-control rounded-0 ${errors.about ? 'is-invalid' : ''}`} placeholder="About" />
                            <div className="invalid-feedback">{errors.about?.message}</div>
                        </div>
                        {/* <div className="form-group mb-3">
                            <label>Password:</label>
                            <input type="password" name="password" {...register('password')} className={`form-control rounded-0 ${errors.password ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.password?.message}</div>
                        </div>

                        <div className="form-group mb-3">
                            <label>Confirm Password:</label>
                            <input type="password" name="confirm_password" {...register('confirm_password')} className={`form-control rounded-0 ${errors.confirm_password ? 'is-invalid' : ''}`} />
                            <div className="invalid-feedback">{errors.confirm_password?.message}</div>
                        </div> */}
                        <div className="form-group mb-3">
                            <button type="submit" className="btn btn-primary rounded-0">Submit</button>
                            {/* <button type="button" onClick={reset} className="btn btn-warning float-right ms-2">Reset</button> */}
                        </div>
                    </form>
                </div>

            </div>

        </div>
    </div>
</div>

        </div>
    );
}

export default ViewUsers;
