import React,{useState, useEffect} from "react";
import { Link } from 'react-router-dom';
import axios from "axios";
// import { Link } from 'react-router-dom';
const ViewUsers = () => {

const [loading,setLoading] = useState(true);
const [users, setUsers] = useState([]);

    useEffect(() => {
        axios.get('/api/view-users').then(res=> {
            // console.log("test data",res.data.users);
            if(res.data.status === 200){
                setUsers(res.data.users);
            }
            setLoading(false);
        });
    },[]);

    var viewUsersList_HTMLTABLE = "";
    if(loading){
        return <h4>Loading Users...</h4>
    }
    else{
        viewUsersList_HTMLTABLE = 
        users.map( (item) => {
            return (
                <tr key={item.id}>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>
                        <link to={'edit_user'}>
                        </link>
                    </td>
                    <td>{item.id}</td>
                </tr>
            )
        });
    }
    return (
        <div className="container py-4">
           <div className="card mt-4">
                <div className="card-header">
                    <h4>Users List</h4>
                    <Link to="#" className="btn btn-primary btn-sm float-end">Add User
                    </Link>
                </div>

                <div className="card-body">
                    <table className="table table-bordered table-triped">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Edit</th>
                                <th>Delete</th>

                            </tr>
                        </thead>
                        <tbody>

                        </tbody>
                    </table>
                </div>
           </div>

        </div>
    );
}

export default ViewUsers;
