import React from "react";
import axios from 'axios';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as Yup from 'yup';
import { store } from "react-notifications-component";
import { useNavigate } from "react-router-dom";

function Login() {
    let navigate = useNavigate();
    const onSubmit = data => {

        axios.get('/sanctum/csrf-cookie').then(response => {


                axios.post('/api/login', data)
                .then(res => {
                if(res.data.status === 200){
                    navigate('/admin/dashboard');
                    store.addNotification({
                        title: "Success!",
                        message:res.data.message,
                        type: "success",
                        insert: "top",
                        container: "top-right",
                        animationIn: ["animated", "fadeIn"],
                        animationOut: ["animated", "fadeOut"],
                        dismiss: {
                          duration: 2000,
                          onScreen: false
                        }
                      });
                    } 

                    if(res.data.status === 422){
                        reset({})
                        store.addNotification({

                            title: "Failed!",
                            message: res.data.message,
                            type: "danger",
                            insert: "top",
                            container: "top-right",
                            animationIn: ["animated", "fadeIn"],
                            animationOut: ["animated", "fadeOut"],
                            dismiss: {
                              duration: 3000,
                              onScreen: false
                            }
                          });
                        }

                        if(res.data.status === 402){
                            store.addNotification({

                                title: "Failed!",
                                message: res.data.message,
                                type: "danger",
                                insert: "top",
                                container: "top-right",
                                animationIn: ["animated", "fadeIn"],
                                animationOut: ["animated", "fadeOut"],
                                dismiss: {
                                  duration: 3000,
                                  onScreen: false
                                }
                              });
                            }
                })
                .catch(err => console.log(err));
            });
     };

     const validationSchema = Yup.object().shape({

        email: Yup.string()
            .required('Email is required')
            .email('Email is invalid'),
        password: Yup.string()
            .required('Password is required')
            .min(8, 'Password must be at least 8 characters')
            .max(40, 'Password must not exceed 40 characters'),

    });

    const { register, handleSubmit, reset, formState: { errors } } = useForm({ resolver: yupResolver(validationSchema) });

    return (
        <div className="container py-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h4>Login</h4>
                        </div>
                        <div className="card-body">
                            <form onSubmit={handleSubmit(onSubmit)} method="POST">


                                <div className="form-group mb-3">
                                    <label>Email:</label>
                                    <input type="email" name="email" {...register('email')} className={`form-control ${errors.email ? 'is-invalid' : ''}`} />
                                    <div className="invalid-feedback">{errors.email?.message}</div>
                                </div>

                                <div className="form-group mb-3">
                                    <label>Password:</label>
                                    <input type="password" name="password" {...register('password')} className={`form-control ${errors.password ? 'is-invalid' : ''}`} />
                                    <div className="invalid-feedback">{errors.password?.message}</div>
                                </div>


                                <div className="form-group mb-3">
                                    <button type="submit" className="btn btn-primary">Login</button>
                                    <button type="button" onClick={reset} className="btn btn-warning float-right ms-2">Reset</button>
                                </div>
                            </form>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Login;
